import { useState, useEffect } from 'react'
import ReceptionistItem from './ReceptionistItem'
import Button from '../Button'
import { Link } from "react-router-dom";


const Receptionist = () => {
    const [patients, setPatients] = useState([])
    const [search, setSearch] = useState([])

    useEffect(() => {
        const getPatients = async () => {
            const tasksFromServer = await fetchPatients()
            //   console.log(tasksFromServer)
            setPatients(tasksFromServer)
        }

        getPatients()
    }, [])

    const fetchPatients = async () => {
        const res = await fetch('http://localhost:3000/api/reception/patient/', { method: "GET" })
        const data = await res.json()

        return data
    }


    const data = patients.filter(
        (val) => {
            const name = (val.fname+val.hname+val.m_initial).toLowerCase()
            if (search===""){
                return val
            } else if (name.inludes(search.toLowerCase())){
                return val
            } 
            return null
        }
    ).map((patient, index) => (
        <ReceptionistItem key={index} patient={patient} />))

    return (
        <div className="main-content">

            <div className="search-box" >
                Search: <input type="text" placeholder="Search..." value={search} onChange={(e) => setSearch(e.target.value)} />
                <div className="add-button"><Link to="/edit/reception"><Button text="Add Patients" /></Link></div>
            </div>
            {patients.length > 0 ?
                data
                : 'Nothing to show here'}

        </div>

    )
}

export default Receptionist
