import React,{useState,useEffect} from 'react'
import Button from '../Button'
import { AiOutlineArrowLeft } from 'react-icons/ai'
import { useParams, Link } from 'react-router-dom'
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js'
import { Line } from 'react-chartjs-2'

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
)


const Patient = () => {
    const [patient, setPatient] =useState(null)
    const { id } = useParams()
    const chartData = {
        labels: ['January', 'February', 'March',
            'April', 'May'],
        datasets: [
            {
                label: 'Weight',
                fill: false,
                lineTension: 0.5,
                backgroundColor: 'rgba(75,192,192,1)',
                borderColor: 'rgba(0,0,0,1)',
                borderWidth: 2,
                data: [65, 59, 80, 81, 56]
            }
        ]
    }
    console.log(id)
    const backButton = <div><AiOutlineArrowLeft /><div style={{ fontSize: "12.5px", display: "inline" }}>Back</div></div>

    useEffect(() =>  {
        const fetchPatient = async () => {
            const res = await fetch('http://localhost:3000/api/patients/medical/information/' + id, { method: "GET" })
            const data = await res.json()
            console.log(data)
            setPatient(data)

        }
        fetchPatient()


    }, [id])
    let patientVal= {}
    if (patient===null){
        return (
            <div className="main-content" >
            <div className="item" style={{ fontSize: "15px" }} >
                <Link to="/patient"><Button text={backButton} /></Link>
                <div style={{ width: "100%", display: "block", marginTop: "25px" }}>
                    Nothing to show...yet
                </div>
            </div>
        </div>
        )
    } else {
        patientVal=patient.patient[0]
    }
    console.log(patientVal)
